sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
//Construct a GlideRecord query to find all NeedIt Task Records with due date
//field values in the past for the same assigned to. The records cannot be in the closed state
var rightNow = new GlideDateTime();

var OverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
OverdueNITasks.addQuery('due_date','<=',rightNow);
OverdueNITasks.addQuery('state','<',3);
OverdueNITasks.addQuery('assigned_to',current.assigned_to);

OverdueNITasks.query();
if(OverdueNITasks.next()){
	var RowCount = OverdueNITasks.getRowCount();
}
//Print to a message field on a notification
template.print("In addition to this overdue NeedIt Task, you have " + RowCount + " more overdue NeedIt Tasks.");
//template.print(RowCount);
})(current, template, email, email_action, event);
